mapper.select:
select *
from orders


        and status = #{status}


limit #{page}, #{pageSize}

select count(*)
from orders


        and status = #{status}

select o.*, u.name userName, a.detail addressDetail from orders o left join user u on o.user_id = u.id left join address_book a on o.address_book_id = a.id
and number like concat('%', #{number}, '%')
and phone like concat('%', #{phone}, '%')
and status = #{status}
and order_time between #{beginTime} and #{endTime}
order by order_time desc limit #{page}, #{pageSize}

select count(*)
from orders


        and number like concat('%', #{number}, '%')


        and phone like concat('%', #{phone}, '%')


        and status = #{status}


        and order_time between #{beginTime} and #{endTime}
