mapper.select:
select s.id, s.name, s.category_id, s.price, s.image, s.status, s.update_time, c.name categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%', #{name}, '%')
and s.category_id = #{categoryId}
and s.status = #{status}
order by s.update_time desc limit #{page}, #{pageSize}

select count(*)
from setmeal


        and name like concat('%', #{name}, '%')


        and category_id = #{categoryId}


        and status = #{status}
